client_script:
const captchaText = document.getElementById('captcha-text');
const captchaInput = document.getElementById('captcha-input');
const refreshCaptchaButton = document.getElementById('refresh-captcha');
const nextButton = document.querySelector('.btn-next');

// Function to generate random captcha
function generateCaptcha() {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let captcha = '';
    for (let i = 0; i < 6; i++) {
        captcha += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return captcha;
}

// Function to refresh captcha
function refreshCaptcha() {
    captchaText.textContent = generateCaptcha();
    captchaInput.value = ''; // Clear the captcha input field
}

// Refresh captcha initially and set interval for auto refresh
refreshCaptcha();
setInterval(refreshCaptcha, 30000);

// Event listener for refresh button click
refreshCaptchaButton.addEventListener('click', refreshCaptcha);

// Event listener for next button click
nextButton.addEventListener('click', function(event) {
    event.preventDefault(); // Prevent the default button action

    // Verify captcha
    if (captchaInput.value.trim() === captchaText.textContent.trim()) {
        // Hide the current section and show the next section
        const currentSection = document.querySelector('.form-section.active');
        currentSection.classList.remove('active');
        currentSection.style.display = 'none';

        const nextSection = currentSection.nextElementSibling;
        if (nextSection && nextSection.classList.contains('form-section')) {
            nextSection.classList.add('active');
            nextSection.style.display = 'flex'; // Display next section using flex
        }
    } else {
        alert('CAPTCHA verification failed. Please try again.');
        refreshCaptcha();
    }
});

